SELECT SUM(lo_extendedprice * lo_discount) AS revenue FROM lineorder,dim_date WHERE lo_orderdate = d_datekey AND d_year = 1993 AND lo_discount >= 1 AND lo_discount <= 3 AND lo_quantity < 25;
SELECT SUM(lo_extendedprice * lo_discount) AS revenue FROM lineorder,dim_date WHERE lo_orderdate = d_datekey AND d_yearmonthnum = 199401 AND lo_discount >= 4 AND lo_discount <= 6 AND lo_quantity >= 26 AND lo_quantity <= 35;
SELECT SUM(lo_extendedprice * lo_discount) AS revenue FROM lineorder,dim_date WHERE lo_orderdate = d_datekey AND d_weeknuminyear = 6 AND d_year = 1994 AND lo_discount >= 5 AND lo_discount <= 7 AND lo_quantity >= 26 AND lo_quantity <= 35;
SELECT SUM(lo_revenue), d_year, p_brand1 FROM lineorder, dim_date, part, supplier WHERE lo_orderdate = d_datekey AND lo_partkey = p_partkey AND lo_suppkey = s_suppkey AND p_category = 'MFGR#12' AND s_region = 'AMERICA' GROUP BY d_year, p_brand1 ORDER BY d_year, p_brand1;
SELECT SUM(lo_revenue), d_year, p_brand1 FROM lineorder, dim_date, part, supplier WHERE lo_orderdate = d_datekey AND lo_partkey = p_partkey AND lo_suppkey = s_suppkey AND p_brand1 IN ('MFGR#2221','MFGR#2222','MFGR#2223','MFGR#2224','MFGR#2225','MFGR#2226','MFGR#2227','MFGR#2228') AND s_region = 'ASIA' GROUP BY d_year, p_brand1 ORDER BY d_year, p_brand1;
SELECT SUM(lo_revenue), d_year, p_brand1 FROM lineorder, dim_date, part, supplier WHERE lo_orderdate = d_datekey AND lo_partkey = p_partkey AND lo_suppkey = s_suppkey AND p_brand1 = 'MFGR#2221' AND s_region = 'EUROPE' GROUP BY d_year, p_brand1 ORDER BY d_year, p_brand1;
SELECT c_nation, s_nation, d_year, SUM(lo_revenue) AS revenue FROM customer,lineorder, supplier, dim_date WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_orderdate = d_datekey AND c_region = 'ASIA' AND s_region = 'ASIA' AND d_year >= 1992 AND d_year <= 1997 GROUP BY c_nation, s_nation, d_year ORDER BY d_year ASC, revenue DESC;
SELECT c_city, s_city, d_year, SUM(lo_revenue) AS revenue FROM customer,lineorder, supplier, dim_date WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_orderdate = d_datekey AND c_nation = 'UNITED STATES' AND s_nation = 'UNITED STATES' AND d_year >= 1992 AND d_year <= 1997 GROUP BY c_city, s_city, d_year ORDER BY d_year ASC, revenue DESC;
SELECT c_city, s_city, d_year, SUM(lo_revenue) AS revenue FROM customer,lineorder, supplier, dim_date WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_orderdate = d_datekey AND c_city IN ('UNITED KI1','UNITED KI5') AND s_city IN ('UNITED KI1','UNITED KI5') AND d_year >= 1992 AND d_year <= 1997 GROUP BY c_city, s_city, d_year ORDER BY d_year ASC, revenue DESC;
SELECT c_city, s_city, d_year, SUM(lo_revenue) AS revenue FROM customer,lineorder, supplier, dim_date WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_orderdate = d_datekey AND c_city IN ('UNITED KI1','UNITED KI5') AND s_city IN ('UNITED KI1','UNITED KI5') AND d_yearmonth = 'Dec1997' GROUP BY c_city, s_city, d_year ORDER BY d_year ASC, revenue DESC;
SELECT d_year,c_nation,SUM(lo_revenue - lo_supplycost) AS profit FROM dim_date,customer,supplier,part,lineorder WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_partkey = p_partkey AND lo_orderdate = d_datekey AND c_region = 'AMERICA' AND s_region = 'AMERICA' AND p_mfgr IN ('MFGR#1','MFGR#2') GROUP BY d_year, c_nation ORDER BY d_year, c_nation;
SELECT d_year,s_nation,p_category,SUM(lo_revenue - lo_supplycost) AS profit FROM dim_date,customer,supplier,part,lineorder WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_partkey = p_partkey AND lo_orderdate = d_datekey AND c_region = 'AMERICA' AND s_region = 'AMERICA' AND d_year >= 1997 AND d_year <= 1998 AND p_mfgr IN ('MFGR#1','MFGR#2') GROUP BY d_year, s_nation, p_category ORDER BY d_year, s_nation, p_category;
SELECT d_year,s_city,p_brand1,SUM(lo_revenue - lo_supplycost) AS profit FROM dim_date,customer,supplier,part,lineorder WHERE lo_custkey = c_custkey AND lo_suppkey = s_suppkey AND lo_partkey = p_partkey AND lo_orderdate = d_datekey AND c_region = 'AMERICA' AND s_nation = 'UNITED STATES' AND d_year >= 1997 AND d_year <= 1998 AND p_category = 'MFGR#14' GROUP BY d_year, s_city, p_brand1 ORDER BY d_year, s_city, p_brand1;
